
#!/bin/bash
# This script assumes gcloud is installed as aprt of the Google Cloud SDK and is on the user's PATH enviornment variable
# Ths script uses the Google cloud SDK's command line tool to instantiate VM instances based off of a Deep learning disk that we leveraged from http://cs231n.github.io/gce-tutorial/
gcloud compute --project=cp100-201215 instances create cs525-vm --zone=us-east1-b --machine-type=n1-standard-8 --subnet=default --maintenance-policy=MIGRATE --service-account=485656763584-compute@developer.gserviceaccount.com --scopes=https://www.googleapis.com/auth/devstorage.read_only,https://www.googleapis.com/auth/logging.write,https://www.googleapis.com/auth/monitoring.write,https://www.googleapis.com/auth/servicecontrol,https://www.googleapis.com/auth/service.management.readonly,https://www.googleapis.com/auth/trace.append --min-cpu-platform=Automatic --tags=http-server,https-server --image=cs231n-image --image-project=cp100-201215 --boot-disk-size=40GB --no-boot-disk-auto-delete --boot-disk-type=pd-standard --boot-disk-device-name=cs525-vm
gcloud compute --project=cp100-201215 firewall-rules create default-allow-http --direction=INGRESS --priority=1000 --network=default --action=ALLOW --rules=tcp:80 --source-ranges=0.0.0.0/0 --target-tags=http-server
gcloud compute --project=cp100-201215 firewall-rules create default-allow-https --direction=INGRESS --priority=1000 --network=default --action=ALLOW --rules=tcp:443 --source-ranges=0.0.0.0/0 --target-tags=https-server

gcloud compute --project=cp100-201215 instances create cs525-tejbir-large --zone=us-east1-b --machine-type=n1-standard-8 --subnet=default --maintenance-policy=MIGRATE --service-account=485656763584-compute@developer.gserviceaccount.com --scopes=https://www.googleapis.com/auth/devstorage.read_only,https://www.googleapis.com/auth/logging.write,https://www.googleapis.com/auth/monitoring.write,https://www.googleapis.com/auth/servicecontrol,https://www.googleapis.com/auth/service.management.readonly,https://www.googleapis.com/auth/trace.append --min-cpu-platform=Automatic --tags=http-server,https-server --image=cs231n-image --image-project=cp100-201215 --boot-disk-size=25GB --no-boot-disk-auto-delete --boot-disk-type=pd-ssd --boot-disk-device-name=cs525-tejbir-large